package doge

import (
	"log"
	"reflect"
	"testing"
)

func TestBlock(t *testing.T) {
	// https://dogechain.info/block/fb5f5b5b7d70e660c2c67bca8d3328afae32ae8bb4c8d6cbc42d96ff876b0859
	hex := ""
	expectTxs := 10
	expectValues := []int64{
		1001062713226,
		9999907990000,
		27488096948744,
		9999991000000,
		206330249879929,
		31070000000,
		4818071366605,
		100757668,
		235200003058,
		2037075800,
		18461069000,
		22300000000,
		174339870968,
		89798911754,
		8107180185,
		114852164857,
		1000000000,
		1045842885,
	}
	b := DecodeBlock(hx2b(hex))
	log.Printf("Block: ver %x prev %v merkel %v ntx %v", b.Header.Version, revhex(b.Header.PrevBlock), revhex(b.Header.MerkleRoot), len(b.Tx))
	if len(b.Tx) != expectTxs {
		t.Errorf("TestBlock: wrong number of transactions: %d vs %d", len(b.Tx), expectTxs)
	}
	txOutVals := collectOutVals(&b)
	if !reflect.DeepEqual(txOutVals, expectValues) {
		t.Errorf("TestBlock: incorrect output values found: %v vs %v", txOutVals, expectValues)
	}
}

func collectOutVals(b *Block) (outVals []int64) {
	for _, tx := range b.Tx {
		for _, out := range tx.VOut {
			outVals = append(outVals, out.Value)
		}
	}
	return
}

func revhex(hash []byte) string {
	reverseInPlace(hash)
	return HexEncode(hash)
}
